
public class PrefixPostfix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5;
		int j=8;
		System.out.println("Operation1:result of i++,Postfix of i:"+i+" and i++ is : "+i++);
		System.out.println("after Operation1, value of i:"+i);
		System.out.println("Operation2:result of --j, Prefix of j: "+j+" and --j is:"+--j);
		System.out.println("after Operation2, value od j: "+j);

	}

}
